var _ = require('lodash');
var fs = require('fs');
var gulp = require('gulp');
var gutil = require('gulp-util');
var path = require('path');
var webpack = require('webpack');
var webpackConfig = require('./webpack.conf');
var WebpackDevServer = require('webpack-dev-server');

var FAVICON = new Buffer('', 'base64');


module.exports = function(config) {
  gulp.task('server', function(callback) {
    var compiler = webpack(_.defaultsDeep({
      entry: {
        'test/quill': './test/quill.js'
      },
      output: {
        filename: '[name].js'
      }
    }, webpackConfig));
    var server = new WebpackDevServer(compiler, {
      contentBase: '.build/quill/',
      hot: false,
      proxy: [{
        path: /\/(base|karma)\/*/,
        target: 'http://localhost:' + config.testPort
      }, {
        path: '/develop/*',
        rewrite: function(req, opt) {
          req.url = req.url.slice('/develop'.length);
        },
        target: 'http://localhost:' + config.serverPort
      }],
      stats: {
        assets: false,
        chunks: false,
        colors: true,
        errorDetails: true,
        hash: false,
        timings: false,
        version: false
      }
    });
    server.app.use('/favicon.png', function(req, res, next) {
      res.setHeader('Content-Type', 'image/png');
      res.end(FAVICON);
    });
    server.app.use('/test/fixtures/*.html', function(req, res, next) {
      res.setHeader('Content-Type', 'text/html');
      fs.createReadStream(path.join(__dirname, '..', req.baseUrl)).pipe(res);
    })
    server.listen(config.serverPort, 'localhost', function(err) {
      if (err) throw new gutil.PluginError("webpack-dev-server", err);
      gutil.log("[webpack-dev-server] listening on", config.serverPort);
      callback();
    });
    process.on('SIGINT', function() {
      server.close();
      process.exit(0);
    });
  });

  gulp.task('watch', function() {
    gulp.watch('examples/*.jade', ['examples']);
    gulp.watch('src/**/*.styl', ['theme']);
  });
};
